import unittest
from main import Solution


class TestSolution(unittest.TestCase):

    def setUp(self):
        self.solution = Solution()
        return super().setUp()

    def test_start(self):
        self.assertEqual(7, self.solution.longestPalindrome("abccccdd"))
        self.assertEqual(1, self.solution.longestPalindrome("a"))
        self.assertEqual(1, self.solution.longestPalindrome("abcd"))
        self.assertEqual(3, self.solution.longestPalindrome("aAbb"))
        self.assertEqual(983, self.solution.longestPalindrome(""))


if __name__ == "__main__":
    unittest.main()
